import { React } from "react";
import { Button} from "reactstrap";
import CartProduct from "./CartProduct";
import '../Styles/Cart.css'
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";


function Cart(props){
    //const dispatch = useDispatch();
    const cartItems = useSelector((state) => state.CartReducer.cartItems);
    const total = useSelector((state) => state.CartReducer.total);
    const nav = useNavigate();
    
    const handleCheckoutButton = () =>{
        nav('/checkout');
    }

    return(
        
        <div className='cart-container'>
            <div className='cart-box'>

            {cartItems.length >= 1 ? 
                        <h2 >Cart Summary</h2>
                    : <h4 style={{alignSelf:'center', justifySelf:'center', marginTop:'10%'}}>Cart is empty</h4>}
                
                
                <div className='cart-items-container'>
                <div className='cart-item-headers'>
                        <span className='item-header'>Product</span>
                        <span className='item-header'>Price</span>
                        <span className='item-header'>Quantity</span>
                    </div>
                    {cartItems.length >= 1 ? 
                        cartItems.map(p =>{
                            return(
                                <CartProduct key={p.brandId + p.imageUrl} product={p}/>
                            )
                        })
                    : null}
                </div>
                <div className="check-out">
                    <span>Shipping and taxes calculated at check out</span>
                    <p className='order-total-tag' style={{fontWeight:'bold'}}>Order Total = ${total.toFixed(2)} </p>
                    <Button color='warning' className='check-out-btn' onClick={handleCheckoutButton}>Checkout</Button>
                
                </div>
            </div>
            
        </div>
    )
}

export default Cart;
