import axios from "axios"
import thunk from "redux-thunk";

const serverUrl = process.env.REACT_APP_SERVER_URL;
console.log(serverUrl);

   const headers = {
    'Access-Control-Allow-Origin' : '*',
    'Access-Control-Allow-Methods':'GET,PUT,POST,DELETE,PATCH,OPTIONS'
    }




export function getAllProducts(type){
    return dispatch =>{
        axios.get(serverUrl + '/api/all' + type, headers)
             .then( result =>{
                
                dispatch(getProducts(result.data))
             })
             .catch(error =>{
                console.log(error);
             })
    }
}

export function getAllProductBrandNames(){
    return dispatch =>{
        axios.get(serverUrl +'/api/allBrands', headers)
             .then( result =>{
                dispatch(getProductBrandNames(result.data))
             })
             .catch(error =>{
                console.log(error);
             })
    }
}


export  function authorization(userInfo){
    return async dispatch =>{
          await axios.post(serverUrl + '/auth/authenticate', userInfo)
             .then(result => {
                    dispatch(authenticateSuccess(result.data))
                    return result.data;
                })
                .catch(error => {
                    dispatch(authenticateFail(error.data))
                    return error.data;
                })
    }
}





export function getProducts(products){
    return{
        type: 'GET_PRODUCTS',
        products
    }
}

export function getProductBrandNames(brands){
    return{
        type: 'GET_BRAND_NAMES',
        brands
    }
}


export function getCart(){
    return{
        type: 'GET_CART'
    }
}

export const addToCart = (product, qty = 0) =>{
    return{
        type: 'ADD_TO_CART',
        product,
        qty
               
    }
}

export const removeFromCart = (product) =>{
    return{
        type: 'REMOVE_FROM_CART',
        product
    }
}

export const removeAllFromCart = (product) =>{
    return{
        type: 'REMOVE_ALL_FROM_CART',
        product
    }
}

export const clearCart = () =>{
    return{
        type: 'CLEAR_CART'
    }
}
 export const shippingFormInfo = (data) =>{
    console.log("SHIPPING INFO ACTION CALLED", data);
    return{
        type: 'SHIPPING_FORM_DATA',
        data
    }
 }

 export const billingFormInfo = (data) =>{
    return{
        type: 'BILLING_FORM_DATA',
        data
    }
 }

 export const paymentFormInfo = (data) =>{
    return{
        type: 'PAYMENT_DATA',
        data
    }
 }

 export const authenticateSuccess = (data) =>{
    return{
        type: 'SUCCESS',
        data
    }
 }

 export const authenticateFail = (data) =>{
    return{
        type: 'ERROR',
        data
    }
 }
