import { React } from "react";
import { Button, Table} from "reactstrap";
import CartProduct from "./CartProduct";
import '../Styles/Cart.css'
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";


function Cart(props){
    //const dispatch = useDispatch();
    const cartItems = useSelector((state) => state.CartReducer.cartItems);
    const total = useSelector((state) => state.CartReducer.total);
    const nav = useNavigate();
    const leftArrow = '/images/left-arrow.png';
    const rightArrow = '/images/right-arrow.png';
    
    const handleCheckoutButton = () =>{
        nav('/checkout');
    }

    return(
        
        
            <div className='cart-box'>
            {cartItems.length >= 1 ? 
                        <div className='cart-headers'>
                            <h2 className='cart-header'>Cart Summary</h2>
                            <div className='cart-items-container'>
                            <Table borderless>
                                <thead>
                                        <tr>
                                            <th style={{width:'20%'}}>
                                                Product
                                            </th>
                                            <th style={{width:'20%'}}>
                                                Price
                                            </th>
                                            <th style={{width:'20%'}}>
                                                Quantity
                                            </th>
                                            <th style={{width:'20%'}}>
                                                Subtotal
                                            </th>
                                            <th style={{width:'20%'}}></th>
                                        </tr>
                                </thead>
                                <tbody>
                                    {cartItems.length >= 1 ? 
                                    cartItems.map(p =>{
                                        return(
                                            <CartProduct key={p.brandId} product={p}/>
                                    )
                                    })
                                    : null}
                                </tbody>
                            </Table>
                            </div>
                        </div>
                    : <h2 style={{alignSelf:'center', justifySelf:'center', marginTop:'20%'}}>Cart is empty</h2>}
                <div className="check-out">
                    <span>Shipping and taxes calculated at check out</span>
                    <p className='order-total-tag' style={{fontWeight:'bold'}}>Order Total = ${total.toFixed(2)} </p>
                    <div className='button-container'>
                        <Button color='none' className='keep-shopping-btn' onClick={handleCheckoutButton}>
                            <img className='left-arrow' src={leftArrow} alt='left arrow'/>
                            Keep Shoping
                            </Button>
                        <Button color='none' className='check-out-btn' onClick={handleCheckoutButton}>
                            Checkout
                            <img className='right-arrow' src={rightArrow} alt='right arrow'/>
                        </Button>
                    </div>
                
                </div>
            </div>
            
        
    )
}

export default Cart;
