import React from "react";
import "../Styles/PopUp.css"

import { Button } from "reactstrap";

function PopUp({status,product}){

    return(
        <div className={"popup-container"}>
            {status ? 
            <div className={"popup-body"}>
                <h3>Success!</h3>
                <img src={product.imageUrl} alt={product.brandName}  className={params.type.toLocaleLowerCase() + '-product-image'}/>
                <p>The item has been added to your cart!</p>
                <Button onClick={() => props.handlePopUp(false)}>Close</Button>
            </div>
            : null}
        </div>
    )
}

export default PopUp;
