import React from 'react'
import Cart from './Cart';
import { Button, Form, FormGroup } from 'reactstrap';
import { useSelector } from 'react-redux';
import { useState } from 'react';
import CartProduct from './CartProduct';

function Checkout(){

    const cartItems = useSelector((state) => state.CartReducer.cartItems);
    const total = useSelector((state) => state.CartReducer.total);
    const[firstName, setFirstName] = useState('');
    const[lastName, setLastName] = useState('');
    const[streetAddress, setStreetAddress] = useState('');
    const[aptOrSuite, setaptOrSuite] = useState('');
    const[city, setCity] = useState('');
    const[postCode, setPostCode] = useState('');

    const handleFormChange = (type) =>{
        switch (key) {
            case value:
                
                break;
        
            default:
                break;
        }
    }




    return(
        <div>
            <div>
                <h5>Shipping</h5>
                <Form>
                    <FormGroup>
                        <input type="text" value={firstName} onChange={() => handleFormChange('fisrtName')} placeholder="First Name"></input>
                    </FormGroup>
                    <FormGroup>
                        <input type="text" value={lastName} onChange={() => handleFormChange('lastName')} placeholder="Last Name"></input>
                    </FormGroup>
                    <FormGroup>
                        <input type="text" value={streetAddress} onChange={() => handleFormChange('streetAddress')} placeholder='Street Address'></input>
                    </FormGroup>
                    <FormGroup>
                        <input type="text" value={aptOrSuite} onChange={() => handleFormChange('aptOrSuite')} placeholder="Apt/Suite/(Optional)"></input>
                    </FormGroup>
                    <FormGroup>
                        <input type="text" value={city} onChange={() => handleFormChange('city')} placeholder='City'></input>
                    </FormGroup>
                    <FormGroup>
                        <input type="text" value={postCode} onChange={() => handleFormChange('postCode')} placeholder='Postal Code'></input>
                    </FormGroup>
                </Form>
            </div>
            <div className='summary-container'>
                <h3>OrderSummary</h3>
                <p></p>
                <Button>Guest Checkout</Button>
                <Button>Member Checkout</Button>
            </div>
            <div className='cart-container'>
            <div className='cart-box'>
                <h2>Cart Summary</h2>
                {cartItems.length >= 1 ? 
                    cartItems.map(p =>{
                        return(
                            <CartProduct key={p.brandId} product={p}/>
                        )
                    })
                : <h5>Cart is empty</h5>}  
                <div className="check-out">
                    <span>Shipping and taxes calculated at check out</span>
                    <p className='order-total-tag' style={{fontWeight:'bold'}}>Order Total = ${total.toFixed(2)} </p>
                    <Button color='warning' className='check-out-btn'>Checkout</Button>
                    
                </div>
            </div>
            
        </div>
        </div>
    )
}

export default Checkout;
