import React from "react";
import { useState } from "react";
import '../Styles/Home.css'
import ShopCard from "./ShopCard";


function Home(){
    const[cardDetails, setCardDetails] = useState([
        {
            title: 'Decks',
            description: `Lorem ipsum dolor sit amet, 
                        consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore 
                        et dolore magna aliqua.`,
            image: 'images/decks/decks.jpg'
        },
        {
            title: 'Trucks',
            description: `Lorem ipsum dolor sit amet, 
                        consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore 
                        et dolore magna aliqua. Non arcu risus quis varius.`,
             image: 'images/trucks/trucks.jpg'
        },
        {
            title: 'Wheels',
            description: `Lorem ipsum dolor sit amet, 
                        consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore 
                        et dolore magna aliqua. Non arcu risus quis varius.`,
            image: 'images/wheels/wheelsTwo.jpg'
        },
        {
            title: 'Bearings',
            description: `Lorem ipsum dolor sit amet, 
                        consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore 
                        et dolore magna aliqua.`,
            image: 'images/bearings/bearingsTwo.jpg'
        }
    ])
    return(
        <div className="home-container">
            {/* <div className='intro-header'>
                <h1>Home</h1>
            </div> */}
            <div className="image-container">
                <h1 className="slogan" style={{borderBottom:"1px solid white", marginTop:'10%'}}>Skate with us</h1>
                <p style={{marginLeft:'15%', alignSelf:'center', fontSize:'3em'}}>and</p>
                <h1 className="slogan"  style={{borderTop:"1px solid white"}}>shred in style</h1>
            </div>
            <h3 className='card-header'>Shop Around</h3>
            
            <div className="cards-container"> 
                {cardDetails ? 
                    cardDetails.map(card =>{
                        return(
                            <ShopCard key={card.title} detail={card}/>
                        )
                    })
                : null}
               
            </div>
        </div>
    )
}

export default Home;
