import React from "react";
import { useState } from "react";
import { Button } from "reactstrap";
import { useEffect } from "react";

function PopUp(props){

    const[open, setOpen] = useState(props.status ? props.status : false);


    const handlePopUpClose = () =>{
        setOpen(!props.status);
    }



   

    useEffect(() => 
    {
        console.log("Pop Up from useEffect", open);
    })
    return(
        <div className={"popup-container"}>
            {open ? 
            <div className={"popup-body"}>
                <h3>Success!</h3>
                <p>The item has been added to your cart!</p>
                <Button onClick={() => props.handlePopUp(false)}>Close</Button>
            </div>
            : null}
        </div>
    )
}

export default PopUp;
