import React, {useState} from "react";
import { useDispatch } from "react-redux";
import { Button, Dropdown, DropdownItem, DropdownMenu, DropdownToggle } from "reactstrap";
import { addToCart, removeAllFromCart, removeFromCart} from "../Actions/Actions";

import '../Styles/CartProduct.css'

function CartProduct({product}){
    const dispatch = useDispatch();
    const[dropdownOpen, setDropdownOpen] = useState(false);
    const toggle = () => setDropdownOpen((prevState) => !prevState);
    

    const handleIncrease = (event) =>{
        event.preventDefault()
        dispatch(addToCart(product))
        
    }

    const handleDecrease = (event) =>{
        event.preventDefault()
        dispatch(removeFromCart(product))
        
    }

    function removeAllProductFromCart(event){
        event.preventDefault();
        dispatch(removeAllFromCart(product))
    }

    const displayProductInfo = () =>{
        
        console.log(product.truckType)
        if(product.truckType){
            return true;
        }
        if(product.bearingsType){
            return true;
        }

        if(product.brandId){
            return true;
        }
        
    }


    return(
        <div className='cart-product-container' key={product.brandId}>
            <div className="product-container">
                <img className='product-image' src={product.imageUrl} alt='product'/>
                { product.truckType ? <span >{product.brandName + ' - '  + product.truckType}</span>: null}
                { product.bearingsType ? <span>{product.brandName + ' - '  + product.bearingsType}</span>: null}
                { product.brandId.match(/(Wheels)/g) ? <span>{product.brandName + ' - ' + product.size + '"'}</span>: null}
                { product.color ? <span>{product.brandName + ' - '  + product.size + '"'}</span>: null}
            </div>
            <p>{"$"+product.price}</p>
            <div className="product-description-container">
                <span></span>
                <Dropdown  className='dropdown' isOpen={dropdownOpen} toggle={toggle} direction={"down"}>
                    <DropdownToggle className="dropdown-title" color='none'>{product.purchaseQuantity}</DropdownToggle>
                    <DropdownMenu style={{width:'300px'}}>      
                        <DropdownItem  className='header-bar-item-dropdown'></DropdownItem>
                    </DropdownMenu>
                </Dropdown>

            </div>
            <Button className='remove-product'color='none' onClick={removeAllProductFromCart}>X</Button>
        </div>
    )
}

export default CartProduct;
