import { React } from "react";
import { Button, Table} from "reactstrap";
import CartProduct from "./CartProduct";
import '../Styles/Cart.css'
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";


function Cart(props){
    //const dispatch = useDispatch();
    const cartItems = useSelector((state) => state.CartReducer.cartItems);
    const total = useSelector((state) => state.CartReducer.total);
    const nav = useNavigate();
    
    const handleCheckoutButton = () =>{
        nav('/checkout');
    }

    return(
        
        <div className='cart-container'>
            <div className='cart-box'>

            {cartItems.length >= 1 ? 
                        
                        <div className='cart-headers'>
                            <h2 className='cart-header'>Cart Summary</h2>
                            <Table >
                                <thead>
                                        <tr>
                                            <th>
                                                Product
                                            </th>
                                            <th>
                                                Price
                                            </th>
                                            <th>
                                                Quantity
                                            </th>
                                            <th></th>
                                        </tr>
                                </thead>
                                <tbody>
                                    {cartItems.length >= 1 ? 
                                    cartItems.map(product =>{
                                        return(
                                            <tr>
                                                <td>
                                                <img className='product-image' src={product.imageUrl} alt='product'/>
                                                { product.truckType ? <span >{product.brandName + ' - '  + product.truckType}</span>: null}
                                                { product.bearingsType ? <span>{product.brandName + ' - '  + product.bearingsType}</span>: null}
                                                { product.brandId.match(/(Wheels)/g) ? <span>{product.brandName + ' - ' + product.size + '"'}</span>: null}
                                                { product.color ? <span>{product.brandName + ' - '  + product.size + '"'}</span>: null}
                                                </td>
                                                <td><p className='product-price'>{"$"+product.price}</p></td>
                                                <td>hello</td>
                                                {/* <CartProduct key={p.brandId} product={p}/> */}
                                            </tr>
                                    )
                                    })
                                    : null}
                                </tbody>
    
                            </Table>
                        </div>
                    : <h2 style={{alignSelf:'center', justifySelf:'center', marginTop:'20%'}}>Cart is empty</h2>}
                <div className='cart-items-container'>
                
                    
                </div>
                <div className="check-out">
                    <span>Shipping and taxes calculated at check out</span>
                    <p className='order-total-tag' style={{fontWeight:'bold'}}>Order Total = ${total.toFixed(2)} </p>
                    <Button color='warning' className='check-out-btn' onClick={handleCheckoutButton}>Checkout</Button>
                
                </div>
            </div>
            
        </div>
    )
}

export default Cart;
