const  AuthReducer = (state={}, action) =>{
    switch (action.type) {
        case 'SUCCESS':
                let validLogin = {...state, token: action.data, isLoggedIn: true, validLogin: true}
                return validLogin;
        case 'ERROR':
            console.log("invalid login")
               let invalidLogin = {...state, loggedIn: false, validLogin: false}
               return invalidLogin;
        default:
            return state;
    }
}

export default AuthReducer;
