import React from "react"
import { useState } from "react";
import { Dropdown,DropdownMenu, DropdownItem, DropdownToggle} from 'reactstrap';
import { useSearchParams } from "react-router-dom";
import '../Styles/Header.css'
import { Link } from "react-router-dom";

const DropDown = (props) =>{
    const[dropdownOpen, setDropdownOpen] = useState(false);
    const toggle = () => setDropdownOpen((prevState) => !prevState);
    const[dropDownItems, setDropDownItems] = useState(props.items)
    let [searchParams, setSearchParams] = useSearchParams();

    // const hadleNav = (item) =>{

    // }

    function handleNav(item) {
      let params = item;
      setSearchParams(params);
    }
    
    return(
      <div>
      <Dropdown key={props.type} className='dropdown' isOpen={dropdownOpen} toggle={toggle} direction={"down"}>
        <DropdownToggle className="dropdown-title" color='none'>{props.type}</DropdownToggle>
        <DropdownMenu >
            <DropdownItem header>------Brands------</DropdownItem>
            <DropdownItem >
                <Link style={{textDecoration:'none', color:'black'}}to={'products/'+ props.type}>View All</Link>
            </DropdownItem>
            { dropDownItems ? 
                dropDownItems.map( item => {
                    return(
                        <DropdownItem  className='header-bar-item-dropdown' onClick={handleNav(item)}>{item}</DropdownItem>
                    ) 
                })
            : null}
        </DropdownMenu>
      </Dropdown>
    </div>
    
    )
  }

  export default DropDown;
