import React from 'react'
import {Form, FormGroup, Input, Button, Label} from 'reactstrap'
import {useState, useSelector} from 'react'
import '../Styles/Login.css'
import { useDispatch } from 'react-redux'
import { authorization } from '../Actions/Actions'




function Login(){
    const dispatch = useDispatch();
    const token = useSelector((state) => state.AuthReducer.token);
    
    
    const[user, setUser] = useState({
        email: '',
        password: ''
    })

    const [failedLogin, setFailedLogin]= useState(false);

    const[disabled, setDisabled] = useState(false)


    const handleNameChange = (e) =>{
        setUser({...user, email: e.target.value})
    }

    const handlePwChange = (e) =>{
        setUser({...user, password: e.target.value})
    }

    const handleLogin = (e) =>{
        e.preventDefault();
        try {
            dispatch(authorization(user))
            setFailedLogin(false);
        } catch (error) {
            setFailedLogin(true);
        }
        
    }


    return(
        <div id='login-container'>
            <Form id='login-form'>
                <h3 style={{marginBottom:'5%'}}>LOGIN</h3>
                <FormGroup id='un-form-group' className='form-groups'>
                    <Label for='userName' style={{fontWeight:'bold'}}>Email</Label>
                    {failedLogin ? <span style={{color:'red'}}>Invalid Email and/or Password</span> : null}
                    <Input id='userName' type='text' value={user.email} onChange={handleNameChange}></Input>
                </FormGroup>
                <FormGroup id='pw-form-group' className='form-groups'>
                    <Label for='pw' style={{fontWeight:'bold'}}>Password</Label>
                    <Input id='pw' type='password' value={user.password} onChange={handlePwChange}></Input>
                </FormGroup>
                <FormGroup id='btn-form-group' className='form-groups'>
                    <Button color='primary' id='login-btn' disabled={disabled} onClick={handleLogin}>LOGIN</Button>
                </FormGroup>
                <span>New User? Create Account!</span>
            </Form>

        </div>
    )
}

export default Login;
