import React from 'react'
import "../Styles/Checkout.css"
import { Button, Form, FormGroup, Input, Row, Col, Progress, Spinner } from 'reactstrap';
import { ProgressBar } from 'react-bootstrap';
import { useSelector } from 'react-redux';
import { useState } from 'react';
import ItemSummary from './ItemSummary';

function Checkout(){

    const cartItems = useSelector((state) => state.CartReducer.cartItems);
    const total = useSelector((state) => state.CartReducer.total);
    const[firstName, setFirstName] = useState('');
    const[lastName, setLastName] = useState('');
    const[streetAddress, setStreetAddress] = useState('');
    const[aptOrSuite, setaptOrSuite] = useState('');
    const[city, setCity] = useState('');
    const[postCode, setPostCode] = useState('');
    const[state, setState] = useState('');
    const[country, setCountry] = useState('');

    const[checkoutProgress, setCheckoutProgress] = useState({
        shipping:10,
        billing: 0,
        payment: 0,
        review: 0
    });

    
    const handleFormChange = (type,e) =>{
        e.preventDefault();
        switch (type) {
            case 'firstName':
                setFirstName(e.target.value);
                break;
            case 'lastName':
                setLastName(e.target.value);
                break;
            case 'streetAddress':
                setStreetAddress(e.target.value);
                break;
            case 'aptOrSuite':
                setaptOrSuite(e.target.value);
                break;
            case 'city':
                setCity(e.target.value);
                break;
            case 'postCode':
                setPostCode(e.target.value);
                break;
            case 'state':
                setState(e.target.value);
                break;
            case 'country':
                setCountry(e.target.value);
                break;
            default:
                break;
        }
    }

    return(
        <div className='checkout-container'>
            
            <div className='form-container' style={{backgroundColor:''}}>
                <div className='progress-steps'>
                    <h5 style={{alignSelf:'flex-start', marginLeft:'5%'}}>1. Shipping</h5>
                    <h5 style={{alignSelf:'center', marginLeft:'5%'}}>2. Billing</h5>
                    <h5 style={{alignSelf:'flex-end', marginLeft:'5%'}}>3. Payment</h5>
                    <h5 style={{alignSelf:'flex-end', marginLeft:'5%'}}>4. Review</h5>
                </div>
                <Progress multi  style={{width:'90%'}}>
                    <Progress bar value={checkoutProgress.shipping} style={{textAlign:'center', fontWeight:'bold'}}>Shipping Complete</Progress>
                    <Progress bar color="success" value={checkoutProgress.billing} style={{textAlign:'center', fontWeight:'bold'}}>Billing Complete</Progress>
                    <Progress bar color="info" value={checkoutProgress.payment} style={{textAlign:'center', fontWeight:'bold'}}>PaymentComplete</Progress>
                    <Progress bar color="info" value={checkoutProgress.review} style={{textAlign:'center', fontWeight:'bold'}}>PaymentComplete</Progress>
                </Progress>
                <div className='form-group'>
                    <Form className='form'>
                    <h5 style={{alignSelf:'flex-start', marginLeft:'5%'}}>Shipping Information</h5>
                    <Row>
                        <FormGroup>
                            <Input type="text" value={country} onChange={(e) => handleFormChange('country',e)} placeholder='Country'></Input>
                        </FormGroup>
                    </Row>
                    <Row>
                        <Col md={6}>
                            <FormGroup >
                                <Input type="text" value={firstName} onChange={(e) => handleFormChange('firstName', e)} placeholder="First Name"></Input>
                            </FormGroup>
                        </Col>
                        <Col md={6}>
                            <FormGroup >
                                <Input type="text" value={lastName} onChange={(e) => handleFormChange('lastName',e)} placeholder="Last Name"></Input>
                            </FormGroup>
                        </Col>
                        </Row>
                        <Row>
                            <FormGroup>
                                <Input type="text" value={streetAddress} onChange={(e) => handleFormChange('streetAddress',e)} placeholder='Street Address'></Input>
                            </FormGroup>
                        </Row>
                        <Row>
                            <FormGroup>
                                <Input type="number" value={aptOrSuite} onChange={(e) => handleFormChange('aptOrSuite',e)} placeholder="Apt/Suite/(Optional)"></Input>
                            </FormGroup>
                    </Row>
                    <Row>
                        <Col>
                            <FormGroup>
                                <Input type="text" value={city} onChange={(e) => handleFormChange('city',e)} placeholder='City'></Input>
                            </FormGroup>
                        </Col>
                        <Col>
                            <FormGroup>
                                <Input type="text" value={state} onChange={(e) => handleFormChange('state',e)} placeholder='State'></Input>
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <FormGroup>
                            <Input type="text" value={postCode} onChange={(e) => handleFormChange('postCode',e)} placeholder='Postal Code'></Input>
                        </FormGroup>
                    </Row>
                    <Row>
                        <FormGroup>
                            <Button>Continue</Button>
                        </FormGroup>
                    </Row>
                </Form>
            </div>
            </div>
            <div className='summary-container'>
                <div className='summary-details'>
                    <h3>OrderSummary</h3>
                    <p>Total: {total.toFixed(2)}</p>
                    <p>Shipping and taxes</p>
                </div>
                <div className='product-list'>
                    <span>Items</span>
                    <div className='scroll-list'>
                        {cartItems.length >= 1 ? 
                            cartItems.map(p =>{
                                return(
                                    <ItemSummary item={p}/>
                                )
                            })
                        : null}  
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Checkout;
