const  AuthReducer = (state={}, action) =>{
    switch (action) {
        case 'SUCCESS':
                let validLogin = {...state, token: action.data, loggedIn: true}
                return validLogin;
        case 'ERROR':
               let invalidLogin = {...state, loggedIn: false}
               return invalidLogin;
        default:
            return state;
    }
}

export default AuthReducer;
