import React from "react";
import "../Styles/PopUp.css"


import { Button } from "reactstrap";

function PopUp({status,product,handlePopUp}){

    return(
        <div className={"popup-container"}>
            {status ? 
            <div className={"popup-body"}>
                <h3>Success!</h3>
                <img src={product.imageUrl} alt={product.brandName} className="decks-product-image"/>
                <span>{product.brandName}</span>
                <span style={{fontWeight:'bold'}}>${product.price.toFixed(2)}</span> 
                <p>The item has been added to your cart!</p>
                <Button onClick={() => handlePopUp(false)}>Close</Button>
            </div>
            : null}
        </div>
    )
}

export default PopUp;
