import React from "react";
import "../Styles/PopUp.css"
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import {addToCart} from '../Actions/Actions'
import { useDispatch } from 'react-redux';
import { Button, Input, Label, ListGroup, ListGroupItem, ListGroupItemHeading } from "reactstrap";

function PopUp({status,product,handlePopUp}){

    const nav = useNavigate();
    const dispatch = useDispatch();
    const[qty, setQty] = useState(0);
    
    const handleViewCart = () =>{
        nav("/Cart")
    }


    const handleAddToCart =(event) =>{
        event.preventDefault();
        dispatch(addToCart(product, product.purchaseQuantity));
        
    }

    const handleQtyChange = (e) =>{
        setQty(e.target.value);
    }

   


    return(
        <div className="popup-container">
            {status ? 
            <div className="popup-body">
                <span>{product.brandName} - {product.size ? product.size + "\"" 
                                            : product.truckType ?  product.truckType 
                                            : product.bearingsType ? product.bearingsType
                                            : null}</span>
                <Button onClick={() => handlePopUp(false)} color='danger' style={{color:'white', fontWeight:'bold', alignSelf:'flex-end'}}>X</Button>
                <div id='product-info' className='product-popup-info'>
                    <img src={product.imageUrl} alt={product.brandName} className="product-image-popup"/>
                
                    <div id='product-features' className='product-details'>
                        
                       <ListGroup>
                        <ListGroupItemHeading>Features:  </ListGroupItemHeading>
                            <ListGroupItem>Shape - Popsicle</ListGroupItem>
                            <ListGroupItem>Deck Construction - traditional Maple</ListGroupItem>
                            <ListGroupItem>8.5" Width x 32" Length - Wheelbase: 14.25"</ListGroupItem>
                       </ListGroup>
                       <ListGroup>
                        <ListGroupItemHeading>Price:</ListGroupItemHeading>
                            <ListGroupItem>${product.price.toFixed(2)}</ListGroupItem>
                           
                       </ListGroup>
                      
                        
                
                        <span style={{fontWeight:'bold'}}>${product.price.toFixed(2)}</span>
                    </div> 
                </div>
                <div className="buttons">
                    <Label>Qty:</Label>
                    <Input type='number' value={qty} onChange={handleQtyChange}></Input>
                    <Button color='warning' 
                            size='md' 
                            style={{width:'80%', color:'white',marginTop:'5%', fontWeight:'bold'}}
                            onClick={handleAddToCart}
                    >
                        Add to cart
                    </Button>
             
                </div>
            </div>
            : null}
           {/* <Button onClick={handleViewCart} color='warning' style={{color:'white', fontWeight:'bold'}}>View Cart</Button> */}
        </div>
    )
}

export default PopUp;
