import React, { useEffect, useState } from 'react'
import { Modal, Button, ModalBody, ModalFooter, ModalHeader, ModalTitle} from 'reactstrap';
import axios from 'axios';
import Sort from './Sort';
import ProductCard from './ProductCard';
import Product from './Product';
import PopUp from './PopUp';
import { useParams } from 'react-router-dom';
import '../Styles/Products.css'


function ProductPage({cartProducts,addProducts}){
    const [products, setProducts] = useState();
    const[prods, setProds] = useState();
    const[filterType, setFilterType] = useState();
    const[popup, setPopUp] = useState(false);
    const[productInfo, setProductInfo] = useState();
    const[modalShow, setModalShow] = useState(false)
    let params = useParams()


    useEffect(() =>{
        window.scrollTo(0, 0);
        axios.get('http://localhost:8765/all' + params.type)
             .then( result =>{
                setProds(result.data);
                setProducts(result.data);
             })
             .catch(error =>{
                console.log(error);
             })
    },[params.type])


    useEffect(() =>{
        setFilterType();
    },[params, products])


    useEffect(() =>{
        window.scrollTo(0, 0);
    },[popup])

    
    const handlePopUp = (isOpen) =>{
        setPopUp(isOpen);
        
    }

    const getProductInfo = (product = new Product()) =>{
        setProductInfo(product);
    }


    const handleModalShow = (val) =>{
        setModalShow(val);
    }


    const convertToProduct = (p) =>{
        let prods = Product();
        prods.brandId = p.brandId;
        prods.brandName = p.brandName;
        prods.imageUrl = p.imageUrl;
        prods.size = p.size;
        prods.price = p.price;
        prods.color = p.color;
        prods.quantity = p.quantity;
        prods.truckType = p.truckType;
        prods.purchaseQuantity = 1;
        prods.bearingsType = p.bearingsType;
        return prods;
    }

    
    const setFilter = (category, value) =>{
        if(category === 'brand'){
            setFilterType('Brand - ' + value.p)
            setProds(products.filter(p => p.brandName === value.p));
        } else if(category === 'size'){
            setFilterType('Size - ' + value.p)
            setProds(products.filter(p => p.size === value.p));
        } else if(category === 'color'){
            setFilterType('Color - ' + value.p)
            setProds(products.filter(p => p.color === value.p));
        } else{
            setFilterType()
            setProds(products);
        }
    }



  
    
    return(
        <div className='products-page-container'>
            <div className='sort-container'>
                <Sort products={products} 
                      type={params.type} 
                      setFilter={setFilter} 
                      filterType={filterType}/>
            </div>
            { popup ? 
                    <PopUp status={popup} handlePopUp={handlePopUp} handleModalShow= {handleModalShow} product={productInfo}></PopUp>   
                    : null
                }
                
            <div className={popup ? 'popup-open' : 'products-container'}>
            { modalShow ? 
                   <Modal show={true} onHide={() => handleModalShow(true)} animation={false}>
                   <ModalHeader closeButton>
                    
                   </ModalHeader>
                   <ModalBody>Woohoo, you're reading this text in a modal!</ModalBody>
                   <ModalFooter>
                     <Button variant="secondary" onClick={() => handleModalShow(false)}>
                       Close
                     </Button>
                     <Button variant="primary" onClick={() => handleModalShow(false)}>
                       Save Changes
                     </Button>
                   </ModalFooter>
                 </Modal>
                    : <h1>NOT TRUE MODAL</h1>
                }
                <h1 className='product-type-header'>Skateboard {params.type}</h1>
                <p>Search Results: {prods ? prods.length : null}</p>
                <div className={params.type.toLowerCase() + '-products'}>
                    {prods ? 
                        prods.map( p =>{
                            let cartProds = convertToProduct(p)
                            return(
                                <ProductCard key={cartProds.brandId} 
                                        product={cartProds} 
                                        addProducts={addProducts}
                                        cartProducts={cartProducts}
                                        handlePopUp={handlePopUp}
                                        getProductInfo={getProductInfo}
                                        
                                    />
                            )
                        })
                    : null}
                </div>
            </div>
        </div>
    )
}

export default ProductPage;
